import React, { useState } from "react";
import "./App.css";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import AuthContext from "./api/AuthContext";
import Register from "./pages/Register";
import logo from "./logo.png";
import Login from "./pages/Login";
import TutoringList from "./pages/TutoringList";
import NewTutoring from "./pages/NewTutoring";
import Textbooks from "./pages/Textbooks";
import UploadResource from "./pages/UploadResource";

function App() {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);

  const login = (user, token) => {
    setUser(user);
    setToken(token);
  };

  const logout = () => {
    setUser(null);
    setToken(null);
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      <Router>
        <nav style={{ padding: "1em", borderBottom: "1px solid #ccc" }}>
          <Link to="/"img src={logo} alt="UniMate Logo" className="logo" />
          <Link to="/" style={{ marginRight: "1em" }}>Tutoring</Link>
          <Link to="/new-tutoring" style={{ marginRight: "1em" }}>Post Tutoring</Link>
          <Link to="/textbooks" style={{ marginRight: "1em" }}>Textbooks</Link>
          <Link to="/upload" style={{ marginRight: "1em" }}>Upload Resource</Link>
          {!user && (
            <>
              <Link to="/register" style={{ marginRight: "1em" }}>Register</Link>
              <Link to="/login">Login</Link>
            </>
          )}
          {user && (
            <button onClick={logout} style={{ marginLeft: "1em" }}>Logout</button>
          )}
        </nav>
        <div style={{ padding: "1em" }}>
          <Routes>
            <Route path="/" element={<TutoringList />} />
            <Route path="/new-tutoring" element={<NewTutoring />} />
            <Route path="/textbooks" element={<Textbooks />} />
            <Route path="/upload" element={<UploadResource />} />
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
