import React, { useEffect, useState } from "react";
import axios from "../api/axios";

export default function TutoringList() {
  const [listings, setListings] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    async function fetchData() {
      const res = await axios.get("/tutoring");
      setListings(res.data);
    }
    fetchData();
  }, []);

  // Filtered listings
  const filtered = listings.filter(l =>
    l.title.toLowerCase().includes(search.toLowerCase()) ||
    l.subject.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>Tutoring Listings</h2>

      <input
        placeholder="Search by subject or title..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{
          width: "100%",
          padding: "0.75em",
          marginBottom: "1em",
          border: "1px solid #ccc",
          borderRadius: "4px"
        }}
      />

      {filtered.map(listing => (
        <div key={listing._id} className="listing">
          <h4>{listing.title}</h4>
          <p>{listing.description}</p>
          <p>{listing.subject} - R{listing.pricePerHour}/hour</p>
          <p>By: {listing.createdBy.name}</p>
        </div>
      ))}

      {filtered.length === 0 && <p>No listings found.</p>}
    </div>
  );
}
