import React from "react";
import { Link } from "react-router-dom";
import "./Landing.css";

export default function Landing() {
  const testimonials = [
    {
      name: "Thabo M.",
      quote: "UniMate helped me find a great tutor in just a few minutes!",
    },
    {
      name: "Lebo K.",
      quote: "I sold my old textbooks fast and easily. Highly recommended!",
    },
    {
      name: "Asanda P.",
      quote: "The study resources are amazing. I passed because of UniMate!",
    },
  ];

  const featuredTutors = [
    { name: "Sipho D.", subject: "Math & Physics", rating: "‚≠ê 4.8" },
    { name: "Naledi B.", subject: "English Literature", rating: "‚≠ê 5.0" },
    { name: "Kabelo T.", subject: "Computer Science", rating: "‚≠ê 4.9" },
  ];

  const featuredResources = [
    { title: "Intro to Economics Notes", type: "PDF" },
    { title: "Calculus Past Papers", type: "ZIP" },
    { title: "APA Referencing Guide", type: "DOCX" },
  ];

  return (
    <div className="landing-container">
      <div className="landing-hero">
        <h1>Welcome to UniMate üéì</h1>
        <p>Your all-in-one student platform for tutoring, textbooks, and resources.</p>
        <div className="landing-buttons">
          <Link to="/register" className="landing-btn primary">Get Started</Link>
          <Link to="/login" className="landing-btn">Login</Link>
        </div>
      </div>

      <div className="landing-section">
        <h2>üí¨ What Students Say</h2>
        <div className="cards testimonials">
          {testimonials.map((t, i) => (
            <div key={i} className="card">
              <p>"{t.quote}"</p>
              <strong>- {t.name}</strong>
            </div>
          ))}
        </div>
      </div>

      <div className="landing-section">
        <h2>üåü Featured Tutors</h2>
        <div className="cards">
          {featuredTutors.map((t, i) => (
            <div key={i} className="card">
              <h4>{t.name}</h4>
              <p>{t.subject}</p>
              <p>{t.rating}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="landing-section">
        <h2>üìö Popular Study Resources</h2>
        <div className="cards">
          {featuredResources.map((r, i) => (
            <div key={i} className="card">
              <h4>{r.title}</h4>
              <p>Format: {r.type}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
