import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link,
  useLocation
} from "react-router-dom";
import { FaComment, FaTimes } from "react-icons/fa";
import "./App.css";
import AuthContext from "./api/AuthContext";

// Import your pages
import Landing from "./pages/Landing";
import Register from "./pages/Register";
import Login from "./pages/Login";
import TutoringList from "./pages/TutoringList";
import NewTutoring from "./pages/NewTutoring";
import Textbooks from "./pages/Textbooks";
import UploadResource from "./pages/UploadResource";
import Success from "./pages/Success";
import Cancel from "./pages/Cancel";
import ProtectedRoute from "./components/ProtectedRoute";

import logo from "./logo.jpg";

// Chat Component (can be moved to separate file if preferred)
const FloatingChatButton = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      setMessages([...messages, { text: newMessage, sender: 'user' }]);
      setNewMessage("");
      // Here you would typically send the message to your backend/Socket.io
    }
  };

  return (
    <div className="floating-chat-container">
      {isOpen ? (
        <div className="chat-box">
          <div className="chat-header">
            <h3>UniMate Chat</h3>
            <button 
              onClick={() => setIsOpen(false)}
              className="chat-close-button"
            >
              <FaTimes />
            </button>
          </div>
          <div className="chat-messages">
            {messages.map((msg, index) => (
              <div key={index} className={`message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
          </div>
          <div className="chat-input-container">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
              className="chat-input"
              placeholder="Type your message..."
            />
            <button 
              onClick={handleSendMessage}
              className="chat-send-button"
            >
              Send
            </button>
          </div>
        </div>
      ) : (
        <button 
          onClick={() => setIsOpen(true)}
          className="floating-chat-button"
        >
          <FaComment size={24} />
        </button>
      )}
    </div>
  );
};

// Navbar component (unchanged)
function Navbar({ token, user, logout }) {
  const location = useLocation();
  if (location.pathname === "/") return null;

  return (
    <nav>
      <Link to="/">
        <img src={logo} alt="UniMate Logo" className="logo" />
      </Link>
      <Link to="/tutoring">Tutoring</Link>
      <Link to="/textbooks">Textbooks</Link>

      {token ? (
        <>
          <Link to="/new-tutoring">Post Tutoring</Link>
          <Link to="/upload">Upload Resource</Link>
          <span style={{ color: "#ecf0f1", marginLeft: "1em" }}>
            Hi, {user?.name}
          </span>
          <button
            onClick={logout}
            style={{
              marginLeft: "1em",
              background: "transparent",
              border: "1px solid #ecf0f1",
              color: "#ecf0f1",
              padding: "0.3em 0.6em",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            Logout
          </button>
        </>
      ) : (
        <>
          <Link to="/register">Register</Link>
          <Link to="/login">Login</Link>
        </>
      )}
    </nav>
  );
}

function App() {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);

  const login = (user, token) => {
    setUser(user);
    setToken(token);
  };

  const logout = () => {
    setUser(null);
    setToken(null);
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      <Router>
        <Navbar token={token} user={user} logout={logout} />
        <div style={{ padding: "1em" }}>
          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/tutoring" element={<TutoringList />} />
            <Route path="/textbooks" element={<Textbooks />} />
            <Route
              path="/new-tutoring"
              element={
                <ProtectedRoute>
                  <NewTutoring />
                </ProtectedRoute>
              }
            />
            <Route
              path="/upload"
              element={
                <ProtectedRoute>
                  <UploadResource />
                </ProtectedRoute>
              }
            />
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />
            <Route path="/success" element={<Success />} />
            <Route path="/cancel" element={<Cancel />} />
          </Routes>
        </div>
        {/* Add Floating Chat Button to all pages except Landing */}
        {useLocation().pathname !== "/" && <FloatingChatButton />}
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
