import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import "./App.css";
import AuthContext from "./api/AuthContext";
import Register from "./pages/Register";
import Login from "./pages/Login";
import TutoringList from "./pages/TutoringList";
import NewTutoring from "./pages/NewTutoring";
import Textbooks from "./pages/Textbooks";
import ProtectedRoute from "./components/ProtectedRoute";
import UploadResource from "./pages/UploadResource";

// Import your logo
import logo from "./logo.jpg";

function App() {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);

  const login = (user, token) => {
    setUser(user);
    setToken(token);
  };

  const logout = () => {
    setUser(null);
    setToken(null);
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      <Router>
        <nav>
  <Link to="/">
    <img src={logo} alt="UniMate Logo" className="logo" />
  </Link>
  <Link to="/">Tutoring</Link>
  <Link to="/textbooks">Textbooks</Link>
  {token && (
    <>
      <Link to="/new-tutoring">Post Tutoring</Link>
      <Link to="/upload">Upload Resource</Link>
      <button onClick={logout} style={{ marginLeft: "1em" }}>
        Logout
      </button>
    </>
  )}
  {!token && (
    <>
      {user ? (
  <>
    <span style={{ color: "#ecf0f1", marginLeft: "1em" }}>
      Hi, {user.name}
    </span>
    <button
      onClick={logout}
      style={{
        marginLeft: "1em",
        background: "transparent",
        border: "1px solid #ecf0f1",
        color: "#ecf0f1",
        padding: "0.3em 0.6em",
        borderRadius: "4px",
        cursor: "pointer"
      }}
    >
      Logout
    </button>
  </>
) : (
  <>
    <Link to="/register">Register</Link>
    <Link to="/login">Login</Link>
  </>
)}

    </>
  )}
</nav>

        <div style={{ padding: "1em" }}>
          <Routes>
            <Route path="/" element={<TutoringList />} />
            <Route path="/textbooks" element={<Textbooks />} />
            <Route
  path="/new-tutoring"
  element={
    <ProtectedRoute>
      <NewTutoring />
    </ProtectedRoute>
  }
/>
<Route
  path="/upload"
  element={
    <ProtectedRoute>
      <UploadResource />
    </ProtectedRoute>
  }
/>

            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
